import { HttpClientModule } from '@angular/common/http';
import { HttpClientTestingModule } from '@angular/common/http/testing';
import { async, ComponentFixture, TestBed } from '@angular/core/testing';
import { MatChipInputEvent } from '@angular/material/chips';
import { BrowserAnimationsModule } from '@angular/platform-browser/animations';
import { MaterialModule } from '@app/angular-material';
import { CanvasTestHelper } from '@app/classes/canvas-test-helper';
import { HttpResponse } from '@app/classes/http-response';
import { CommunicationSubscriptionService } from '@app/services/communication/communication-subscriptions/communication-subscription.service';
import { DrawingService } from '@app/services/drawing/drawing.service';
import { NotificationService } from '@app/services/notification/notification.service';
import { of } from 'rxjs';
import { GalleryWindowComponent } from './gallery-window.component';
import SpyObj = jasmine.SpyObj;

interface Tag {
    name: string;
}

// tslint:disable:no-string-literal
// tslint:disable:no-magic-numbers
// tslint:disable:no-any
// tslint:disable:max-file-line-count
describe('GalleryWindowComponent', () => {
    let component: GalleryWindowComponent;
    let fixture: ComponentFixture<GalleryWindowComponent>;
    let subscriptionServiceSpy: SpyObj<CommunicationSubscriptionService>;
    let notificationServiceSpy: SpyObj<NotificationService>;
    let drawServiceSpy: jasmine.SpyObj<DrawingService>;

    let baseCtxStub: CanvasRenderingContext2D;
    let previewCtxStub: CanvasRenderingContext2D;
    let selectorCtxStub: CanvasRenderingContext2D;
    let canvasTestHelper: CanvasTestHelper;
    let canvasStub: HTMLCanvasElement;

    const message: any[] = [
        {
            title: 'dessin4',
            body: {
                tags: ['trois'],
                url:
                    'data:image/png;base64,',
                id: '6054b5cf9000d422883be84b',
                name: 'dessin4',
            },
        },
    ];

    const imagestoShow: any[] = [
        {
            tags: ['trois'],
            url:
                'data:image/png;base64,',
            id: '6054b5cf9000d422883be84b',
            name: 'dessin4',
        },
        {
            tags: ['ok'],
            url:
                'data:image/png;base64,',
            id: '6054b6599000d422883be84c',
            name: 'dessin',
        },
        {
            tags: ['ok'],
            url:
                'data:image/png;base64,',
            id: '6054b6699000d422883be84d',
            name: 'dessin',
        },
    ];

    const filteredImages: any = [
        {
            tags: ['trois'],
            url:
                'data:image/png;base64,',
            id: '6054b5cf9000d422883be84b',
            name: 'dessin4',
        },
        {
            tags: ['ok'],
            url:
                'data:image/png;base64,',
            id: '6054b6599000d422883be84c',
            name: 'dessin',
        },
        {
            tags: ['ok'],
            url:
                'data:image/png;base64,',
            id: '6054b6699000d422883be84d',
            name: 'dessin',
        },
        {
            tags: ['ok'],
            url:
                'data:image/png;base64,',
            id: '6054b6e09000d422883be84e',
            name: 'dessin6',
        },
    ];

    beforeEach(async(() => {
        drawServiceSpy = jasmine.createSpyObj('DrawingService', ['clearCanvas', 'canvasIsBlank', 'updateSize', 'updateCanvasSize']);

        subscriptionServiceSpy = jasmine.createSpyObj('CommunicationSubscriptionService', [
            'getImages',
            'imageDeleteEventListener',
            'imageGetterEventListener',
            'deleteImage',
        ]);

        notificationServiceSpy = jasmine.createSpyObj('NotificationService', ['openSnackBar']);

        TestBed.configureTestingModule({
            declarations: [GalleryWindowComponent],
            imports: [HttpClientModule, MaterialModule, HttpClientTestingModule, BrowserAnimationsModule],
            providers: [
                { provide: CommunicationSubscriptionService, useValue: subscriptionServiceSpy },
                { provide: NotificationService, useValue: notificationServiceSpy },
                { provide: DrawingService, useValue: drawServiceSpy },
            ],
        }).compileComponents();

        canvasTestHelper = TestBed.inject(CanvasTestHelper);
        baseCtxStub = canvasTestHelper.canvas.getContext('2d') as CanvasRenderingContext2D;
        previewCtxStub = canvasTestHelper.canvas.getContext('2d') as CanvasRenderingContext2D;
        selectorCtxStub = canvasTestHelper.canvas.getContext('2d') as CanvasRenderingContext2D;
        canvasStub = canvasTestHelper.canvas;

        drawServiceSpy = TestBed.inject(DrawingService) as jasmine.SpyObj<DrawingService>;
        subscriptionServiceSpy = TestBed.inject(CommunicationSubscriptionService) as jasmine.SpyObj<CommunicationSubscriptionService>;
        subscriptionServiceSpy.imageDeleteEventListener.and.returnValue(of(new HttpResponse(true, '')));
        subscriptionServiceSpy.imageGetterEventListener.and.returnValue(of(new HttpResponse(true, '', undefined, message)));
    }));

    beforeEach(() => {
        fixture = TestBed.createComponent(GalleryWindowComponent);
        component = fixture.componentInstance;
        component['drawingService'].baseCtx = baseCtxStub;
        component['drawingService'].previewCtx = previewCtxStub;
        component['drawingService'].selectorCtx = selectorCtxStub;
        component['drawingService'].canvas = canvasStub;
        canvasStub.width = 800;
        canvasStub.height = 1000;
        fixture.detectChanges();
    });

    it('1. should create', () => {
        expect(component).toBeTruthy();
    });

    it('3. should add tag to tag array', async () => {
        const inputElem = document.getElementById('tagInput') as HTMLInputElement;
        spyOn(component, 'validateTags').and.returnValue(true);

        inputElem.value = 'test';
        const event: MatChipInputEvent = {
            value: 'tagtest',
            input: inputElem,
        };

        component.tags = [];
        component.add(event);
        expect(component.tags.length).toEqual(1);
    });

    it('3. should not add tag if null', async () => {
        const inputElem = document.getElementById('tagInput') as HTMLInputElement;
        spyOn(component, 'validateTags').and.returnValue(false);
        inputElem.value = 'test';
        const event: MatChipInputEvent = {
            value: '',
            input: inputElem,
        };
        component.tags = [];
        component.add(event);
        expect(component.tags.length).toEqual(0);
    });

    it('4. should remove tag to tag array', () => {
        const tagToRemove: Tag = {
            name: 'tag1',
        };
        component.tags = [tagToRemove];

        component.remove(tagToRemove);
        expect(component.tags.indexOf(tagToRemove)).toEqual(-1);
    });

    it('5. calling getDrawings should set to loading and call sub getImages', () => {
        component.getDrawings();
        expect(component.isLoading).toEqual(true);
        expect(subscriptionServiceSpy.getImages).toHaveBeenCalled();
    });

    it('6. should call notification service if get images return errors', () => {
        subscriptionServiceSpy.imageGetterEventListener.and.returnValue(of(new HttpResponse(false, '', undefined, [])));
        component.getDrawings();
        expect(component.isLoading).toEqual(true);
        expect(subscriptionServiceSpy.getImages).toHaveBeenCalled();
        expect(notificationServiceSpy.openSnackBar).toHaveBeenCalled();
    });

    it('7. should format if request return images array', () => {
        jasmine.clock().install();
        component.getDrawings();

        jasmine.clock().tick(100);

        expect(component.filteredImages.length).toEqual(1);
        jasmine.clock().uninstall();
    });

    it('8. should should load image on canvas', () => {
        const spyOnCanvasDraw = spyOn(drawServiceSpy.baseCtx, 'drawImage');
        jasmine.clock().install();
        const imageTest = new Image();
        imageTest.src = '';
        imageTest.width = 500;
        imageTest.height = 500;
        component.insertImageOnCanvas(imageTest);
        jasmine.clock().tick(200);
        jasmine.clock().uninstall();
        expect(spyOnCanvasDraw).toHaveBeenCalled();
    });

    it('9. should return if i != 1', () => {
        const spyOnCanvasDraw = spyOn(component, 'insertImageOnCanvas');
        const mouseEvent = {} as MouseEvent;
        component.loadImage(mouseEvent, 2);
        expect(spyOnCanvasDraw).not.toHaveBeenCalled();
    });

    it('10. should call sub for delete image req', () => {
        component.deleteImage(1);
        expect(subscriptionServiceSpy.deleteImage).toHaveBeenCalled();
    });

    it('11. should set next image in images to show', () => {
        component.filteredImages = filteredImages;
        component.imagesToShow = imagestoShow;
        component.nextImage();
        expect(component.imagesToShow[2]).toEqual(filteredImages[0]);
    });

    it('12. should set previous image in images to show', () => {
        component.filteredImages = filteredImages;
        component.imagesToShow = imagestoShow;
        component.previousImage();
        expect(component.imagesToShow[2].name).toEqual(filteredImages[1].name);
    });

    it('13. should call nextImage if allowed with arrowKey', () => {
        const nextSpy = spyOn(component, 'nextImage');

        const rightKey = {
            code: 'ArrowRight',
        } as KeyboardEvent;

        component.preventNext = false;
        component.handleKeyDown(rightKey);
        expect(nextSpy).toHaveBeenCalled();
    });

    it('14. should not call nextImage if allowed with arrowKey', () => {
        const nextSpy = spyOn(component, 'nextImage');

        const rightKey = {
            code: 'ArrowRight',
        } as KeyboardEvent;

        component.preventNext = true;
        component.handleKeyDown(rightKey);
        expect(nextSpy).not.toHaveBeenCalled();
    });

    it('15. should call previous if allowed with arrowKey', () => {
        const previousSpy = spyOn(component, 'previousImage');

        const leftKey = {
            code: 'ArrowLeft',
        } as KeyboardEvent;

        component.preventPrevious = false;
        component.handleKeyDown(leftKey);
        expect(previousSpy).toHaveBeenCalled();
    });

    it('14. should not call nextImage if allowed with arrowKey', () => {
        const previousSpy = spyOn(component, 'previousImage');

        const leftKey = {
            code: 'ArrowLeft',
        } as KeyboardEvent;

        component.preventPrevious = true;
        component.handleKeyDown(leftKey);
        expect(previousSpy).not.toHaveBeenCalled();
    });

    it('15. should filter by tags', () => {
        component.images = filteredImages;
        component.tags = [{ name: 'ok' }];
        component.updateFilter();
        expect(component.filteredImages.length).toEqual(3);
    });

    it('16. should validate tags', () => {
        const titlePass = 'test';
        const titleFail = '@';
        expect(component.validateTags(titlePass)).toBe(true);
        expect(component.validateTags(titleFail)).toBe(false);
    });

    it('17. should not remove tag if it does not exist', () => {
        const tagToRemove: Tag = {
            name: 'tag1',
        };
        component.tags = [{ name: 'ok' }];

        component.remove(tagToRemove);
        expect(component.tags.indexOf(tagToRemove)).toEqual(-1);
    });

    it('19 should subscribe delete on ngOnInit with success', async () => {
        subscriptionServiceSpy.imageDeleteEventListener.and.returnValue(of(new HttpResponse(true, '', '1', undefined)));
        component.ngOnInit();
        fixture.detectChanges();
        expect(subscriptionServiceSpy.imageDeleteEventListener).toHaveBeenCalled();
        expect(notificationServiceSpy.openSnackBar).toHaveBeenCalled();
    });

    it('20. should subscribe delete on ngOnInit with error', async () => {
        subscriptionServiceSpy.imageDeleteEventListener.and.returnValue(of(new HttpResponse(false, '', '1', undefined)));

        component.ngOnInit();
        fixture.detectChanges();
        expect(subscriptionServiceSpy.imageDeleteEventListener).toHaveBeenCalled();
        expect(notificationServiceSpy.openSnackBar).toHaveBeenCalled();
    });

    it('21. should subscribe getter on ngOnInit with error', async () => {
        subscriptionServiceSpy.imageGetterEventListener.and.returnValue(of(new HttpResponse(false, '', undefined, undefined)));

        component.ngOnInit();
        fixture.detectChanges();
        expect(subscriptionServiceSpy.imageGetterEventListener).toHaveBeenCalled();
        expect(notificationServiceSpy.openSnackBar).toHaveBeenCalled();
    });

    it('22. should subscribe get on ngOnInit with error', async () => {
        subscriptionServiceSpy.imageGetterEventListener.and.returnValue(of(new HttpResponse(false, '', undefined, undefined)));

        component.ngOnInit();
        fixture.detectChanges();
        expect(subscriptionServiceSpy.imageGetterEventListener).toHaveBeenCalled();
        expect(notificationServiceSpy.openSnackBar).toHaveBeenCalled();
    });

    it('23. should create a confirm prompt if canvas is not blank', async () => {
        spyOn(window, 'confirm');
        drawServiceSpy.canvasIsBlank.and.returnValue(false);
        const mEvent = {} as MouseEvent;
        component.loadImage(mEvent, 1);
        expect(window.confirm).toHaveBeenCalledWith('Vous avez un dessin en cours, voulez vous le supprimer?');
    });

    it('24. should call createImage if confirmed', async () => {
        spyOn(window, 'confirm').and.returnValue(true);
        const spy = spyOn(component, 'createImage');
        drawServiceSpy.canvasIsBlank.and.returnValue(false);
        const mEvent = {} as MouseEvent;
        component.loadImage(mEvent, 1);
        expect(spy).toHaveBeenCalled();
    });
    it('25. should call not createImage if not confirmed', async () => {
        spyOn(window, 'confirm').and.returnValue(false);
        const spy = spyOn(component, 'createImage');
        drawServiceSpy.canvasIsBlank.and.returnValue(false);
        const mEvent = {} as MouseEvent;
        component.loadImage(mEvent, 1);
        expect(spy).not.toHaveBeenCalled();
    });

    it('26. should call createImage if canvas is blank', async () => {
        const spy = spyOn(component, 'createImage');
        drawServiceSpy.canvasIsBlank.and.returnValue(true);
        const mEvent = {} as MouseEvent;
        component.loadImage(mEvent, 1);
        expect(spy).toHaveBeenCalled();
    });

    it('27. createImage should set image to not null', async () => {
        const mediaElement = {
            currentSrc: '',
        } as HTMLMediaElement;
        const mEvent = ({
            target: mediaElement,
        } as unknown) as MouseEvent;
        component.createImage(mEvent);
        expect(component.images).toBeDefined();
    });
});
